        } catch (error) {
            console.log(error)
        }
    }

  return (
    <div className="columns">
        <div className="column is-half">
            <table className='table is-striped is-fullwidth mt-5'>
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Gender</th>
                        <th>Status</th>
                        <th>Alamat</th>
                        <th>Email</th>
                        <th>Universitas</th>
                        <th>No.Telp</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((user, index)=>{
                        return (
                            <tr key={user._id}>
                            <td>{index + 1}</td>
                            <td>{user.name}</td>
                            <td>{user.gender}</td>
                            <td>{user.status}</td>
                            <td>{user.alamat}</td>
                            <td>{user.email}</td>
                            <td>{user.univ}</td>
                            <td>{user.nohp}</td>
                            <td>
                                <Link to={`/edit/${user._id}`} className='button is-success is-small'>Edit Data</Link>
                                <button onClick={() => deleteData(user._id)} className='button is-success is-small'>delete</button>
                            </td>
                        </tr>
                        )
                        
                    })}
                </tbody>
            </table>
            <div>
                <Link to={'/add'} className='button is-success'>Add New Data</Link>
            </div>
        </div>
    </div>
  )
}

export default ListData;
